(659, 'AA659', ' Observe laboratory operations to ensure:', ' Observe laboratory operations to ensure:', '', '', ''),
(660, 'AA660', 'o Laboratory testing follows written policies and  procedures in pre-analytic, analytic and post-analytic phases of laboratory testing;', 'o Laboratory testing follows written policies and  procedures in pre-analytic, analytic and post-analytic phases of laboratory testing;', '', '', ''),
(661, 'AA661', 'o Laboratory procedures are appropriate for the testing performed;', 'o Laboratory procedures are appropriate for the testing performed;', '', '', ''),
(662, 'AA662', 'o Deficiencies and nonconformities identified are adequately investigated and resolved within the established timeframe.', 'o Deficiencies and nonconformities identified are adequately investigated and resolved within the established timeframe.', '', '', ''),
